webpackJsonp([0xefeee5c18ad],{397:function(t,n){t.exports={data:{post:{id:"/Users/lugang/My Github/LG-Blog-Gatsby/content/posts/2018-07-07--vim-comand/index.md absPath of file >>> MarkdownRemark",html:'<p>自己用的场景还是偏少，所以命令根本记不住。。。备忘把常用的简单指令记一下。</p>\n<h3>基本移动</h3>\n<table>\n<thead>\n<tr>\n<th align="left">h</th>\n<th align="left">j</th>\n<th align="left">k</th>\n<th align="left">l</th>\n</tr>\n</thead>\n<tbody>\n<tr>\n<td align="left">⬅️</td>\n<td align="left">⬇️</td>\n<td align="left">⬆️</td>\n<td align="left">➡️</td>\n</tr>\n</tbody>\n</table>\n<hr>\n<table>\n<thead>\n<tr>\n<th align="center">H</th>\n<th align="center">本屏首行</th>\n</tr>\n</thead>\n<tbody>\n<tr>\n<td align="center"><strong>L</strong></td>\n<td align="center"><strong>本屏末行</strong></td>\n</tr>\n</tbody>\n</table>\n<hr>\n<table>\n<thead>\n<tr>\n<th align="center"><strong>o</strong></th>\n<th align="center"><strong>这一行的最前处</strong></th>\n</tr>\n</thead>\n<tbody>\n<tr>\n<td align="center"><strong>$</strong></td>\n<td align="center"><strong>这一行的最末尾</strong></td>\n</tr>\n</tbody>\n</table>\n<hr>\n<table>\n<thead>\n<tr>\n<th align="left"><strong>G</strong></th>\n<th align="left"><strong>移动到整个文档最后一行</strong></th>\n</tr>\n</thead>\n<tbody>\n<tr>\n<td align="left"><strong>gg</strong></td>\n<td align="left"><strong>移动到整个文档首行，相当于1g</strong></td>\n</tr>\n<tr>\n<td align="left"><strong>n+Enter</strong></td>\n<td align="left"><strong>向下移动n行</strong></td>\n</tr>\n<tr>\n<td align="left"><strong>nG</strong></td>\n<td align="left"><strong>跳转到第n行</strong></td>\n</tr>\n</tbody>\n</table>\n<hr>\n<h3>搜索替换</h3>\n<hr>\n<table>\n<thead>\n<tr>\n<th align="left"><strong>/word</strong></th>\n<th align="left"><strong>在文档中向下搜索’word’</strong></th>\n</tr>\n</thead>\n<tbody>\n<tr>\n<td align="left"><strong>?word</strong></td>\n<td align="left"><strong>在文档中向上搜索’word’</strong></td>\n</tr>\n<tr>\n<td align="left"><strong>n/N</strong></td>\n<td align="left"><strong>按下Enter搜索完后显示下/上一个匹配项</strong></td>\n</tr>\n<tr>\n<td align="left"><strong>:1,$s /word1/word2/g</strong></td>\n<td align="left"><strong>全文替换</strong></td>\n</tr>\n</tbody>\n</table>\n<hr>\n<h3>删除</h3>\n<hr>\n<table>\n<thead>\n<tr>\n<th align="left"><strong>x/X</strong></th>\n<th align="left"><strong>向前/后删除一个字符</strong></th>\n</tr>\n</thead>\n<tbody>\n<tr>\n<td align="left"><strong>dd</strong></td>\n<td align="left"><strong>删除整行（ndd）</strong></td>\n</tr>\n<tr>\n<td align="left"><strong>yy</strong></td>\n<td align="left"><strong>复制整行（nyy）</strong></td>\n</tr>\n<tr>\n<td align="left"><strong>p/P</strong></td>\n<td align="left"><strong>粘贴在光标上/下一行处</strong></td>\n</tr>\n</tbody>\n</table>\n<hr>\n<h3>撤销</h3>\n<hr>\n<table>\n<thead>\n<tr>\n<th align="left"><strong>u</strong></th>\n<th align="left"><strong>复原上一个动作</strong></th>\n</tr>\n</thead>\n<tbody>\n<tr>\n<td align="left"><strong>ctrl+r</strong></td>\n<td align="left"><strong>重做上一个动作</strong></td>\n</tr>\n</tbody>\n</table>\n<hr>\n<h3>模式</h3>\n<hr>\n<table>\n<thead>\n<tr>\n<th align="left"><strong>:w</strong></th>\n<th align="left"><strong>保存</strong></th>\n</tr>\n</thead>\n<tbody>\n<tr>\n<td align="left"><strong>:w!</strong></td>\n<td align="left"><strong>强存（权限问题）</strong></td>\n</tr>\n<tr>\n<td align="left"><strong>:q</strong></td>\n<td align="left"><strong>退出</strong></td>\n</tr>\n<tr>\n<td align="left"><strong>:q!</strong></td>\n<td align="left"><strong>强退（不保存）</strong></td>\n</tr>\n<tr>\n<td align="left"><strong>:wq</strong></td>\n<td align="left"><strong>保存完走</strong></td>\n</tr>\n</tbody>\n</table>\n<hr>',fields:{slug:"/vim-comand/",prefix:"2018-07-07"},frontmatter:{title:"VIM常用指令备忘",author:"todaylg",category:"备忘",cover:{childImageSharp:{resize:{src:"/LG-Blog-Gatsby/static/test5-6e63f45c61cd9143a38fd80960b786f7-160fa.png"}}}}},authornote:{id:"/Users/lugang/My Github/LG-Blog-Gatsby/content/parts/author.md absPath of file >>> MarkdownRemark",html:"<p><strong>todaylg</strong> </p>"},site:{siteMetadata:{facebook:{appId:""}}}},pathContext:{slug:"/vim-comand/",prev:{id:"/Users/lugang/My Github/LG-Blog-Gatsby/content/posts/2018-07-06--css-problem/index.md absPath of file >>> MarkdownRemark",fields:{slug:"/css-problem/",prefix:"2018-07-06"},frontmatter:{title:"CSS问题集",category:"备忘"}}}}}});
